import React, { useState } from "react";
import { useLocalState } from "../util/useLocalStrorage";
//import { ajax } from "../Services/fetchService";
import { Button, Col, Container, Row, Form } from "react-bootstrap";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [jwt, setJwt] = useLocalState("", "jwt");

  function sendLoginRequest() {
    const reqBody = {
      username: username,
      password: password,
    };
    // ajax("api/auth/login", "POST", jwt, reqBody);
    fetch("api/auth/login", {
      headers: {
        "Content-Type": "application/json",
      },
      method: "post",
      body: JSON.stringify(reqBody),
    })
      .then((response) => {
        if (response.status === 200)
          return Promise.all([response.json(), response.headers]);
        else return Promise.reject("Invalid login attempt");
      })
      .then(([body, headers]) => {
        //"authorization" comes from backend HttpStatus.AUTHORIZE=> Authorization
        // It can be seen at the backend or Network->headers tab of the browser
        setJwt(headers.get("authorization"));
        //if logged successfully send to the dashboard page
        window.location.href = "dashboard";
      })
      .catch((message) => {
        alert(message);
      });
  }
  return (
    <>
      <Container>
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label htmlFor="username">Username</Form.Label>
          <Form.Control
            type="email"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label htmlFor="username">Username</Form.Label>
          <Form.Control
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Row>
          <Col>
            <div>
              <Button
                id="submit"
                type="button"
                onClick={() => sendLoginRequest()}
              >
                Login
              </Button>
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Login;
