import { useEffect } from "react";
import "./App.css";
import { useLocalState } from "./util/useLocalStrorage";
import { Route, Routers } from "react-router-dom";
import Dashboard from "./Dashboard";

function App() {
  const [jwt, setJwt] = useLocalState("", "jwt");

  useEffect(() => {
    if (!jwt) {
      const reqBody = {
        username: "trevor",
        password: "asdfasdf",
      };
      fetch("api/auth/login", {
        headers: {
          "Content-Type": "application/json",
        },
        method: "post",
        body: JSON.stringify(reqBody),
      })
        .then((response) => Promise.all([response.json(), response.headers]))
        .then(([body, headers]) => {
          //"authorization" comes from backend HttpStatus.AUTHORIZE=> Authorization
          // It can be seen at the backend or Network->headers tab of the browser
          setJwt(headers.get("authorization"));
          // console.log(jwt);
          // console.log(body);
        });
    }
  }, [jwt, setJwt]);
  return (
    <Routers>
      <Route path="/" element={<Dashboard />} />
    </Routers>
  );
}

export default App;
